import {
  Component,
  OnInit,
  ViewChild,
} from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, map } from 'rxjs';
import { BackendRes } from 'src/app/components/auth/auth.service';
import {
  CreateProductService,
  ModalType,
} from 'src/app/components/create-product-form/create-product.service';
import { Product } from 'src/app/components/products-list/product-item/product-item.component';
import { SearcherParameters } from 'src/app/components/searcher/searcher.component';
import { ToastDirective } from 'src/app/components/toast/toast.directive';
import { ToastService } from 'src/app/components/toast/toast.service';
import { SharedModule } from 'src/app/shared/shared.module';

@Component({
  selector: 'app-dashboard-home',
  templateUrl: './dashboard-home.component.html',
  styleUrls: ['./dashboard-home.component.scss'],
  standalone: true,
  imports: [SharedModule],
})
export class DashboardHomeComponent
  implements OnInit
{
  @ViewChild(ToastDirective, { static: true })
  toast!: ToastDirective;
  error?: { status: number; msg: string } =
    undefined;
  products$!: Observable<Product[] | null>;
  selectedProduct$!: Observable<Product>;
  modal$!: Observable<ModalType>;

  constructor(
    private activatedRoute: ActivatedRoute,
    private createProductService: CreateProductService,
    private toastService: ToastService
  ) {}

  ngOnInit() {
    this.selectedProduct$ =
      this.createProductService.productDetails$;
    this.modal$ =
      this.createProductService.modal$;

    this.products$ =
      this.activatedRoute.data.pipe(
        map(({ products }) => {
          if ('status' in products) {
            this.error = {
              ...products,
            };
            return null;
          }
          return products as Product[];
        })
      );
  }

  onRemoveProduct() {} // compolete it!

  setToast(res: BackendRes) {
    this.toastService.createComponent(
      this.toast,
      res
    );
  }

  onSearch(parameters: SearcherParameters) {
    this.products$ =
      this.activatedRoute.data.pipe(
        map(({ products }) => {
          if (
            parameters.category.toLowerCase() ===
            'all'
          ) {
            return products.filter(
              (product: Product) =>
                `${product.name}${product.model}${product.brand}`.includes(
                  parameters.product
                )
            );
          }

          if (
            parameters.category.toLowerCase() !==
            'all'
          ) {
            return (
              products.filter(
                (product: Product) =>
                  product.category.toLowerCase() ===
                  parameters.category
              ) || null
            );
          }

          return (
            products.filter(
              (product: Product) =>
                `${product.name}${product.model}${product.brand}`.includes(
                  parameters.product
                ) &&
                product.category.toLowerCase() ===
                  parameters.category
            ) || null
          );
        })
      );
  }
}
<app-searcher
  (searcherEmitter)="onSearch($event)"
></app-searcher>

<app-products-list
  *ngIf="products$ | async; else Error"
  [products$]="products$"
>
</app-products-list>

<app-updated-form-modal
  *ngIf="(modal$ | async) === 'update'"
  (toastEmitter)="setToast($event)"
></app-updated-form-modal>

<app-remove-modal
  *ngIf="(modal$ | async) === 'remove'"
  (toastEmitter)="setToast($event)"
></app-remove-modal>

<ng-template #Error>
  <div class="container">
    <span class="lead">{{ error?.msg }}</span>
    <p class="">
      <a
        routerLink="/"
        class="link-primary"
      >
        Click here
      </a>
      to redirect to login page
    </p>
  </div>
</ng-template>
<ng-template appToast></ng-template>
