import { Component, OnInit } from '@angular/core';
import { SharedModule } from '../shared/shared.module';
import { ActivatedRoute } from '@angular/router';
import { UserData } from '../components/auth/auth.service';
import { Observable, map } from 'rxjs';
import {
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';

export interface UserFormProps {
  email: FormControl<string>;
}

@Component({
  selector: 'app-user-data',
  templateUrl: './user-data.component.html',
  styleUrls: ['./user-data.component.scss'],
  standalone: true,
  imports: [SharedModule],
})
export class UserDataComponent implements OnInit {
  userData!: Observable<UserData>;
  userForm!: FormGroup<UserFormProps>;

  constructor(
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.userData = this.activatedRoute.data.pipe(
      map(({ userData }) => {
        return userData;
      })
    );

    this.userForm = new FormGroup({
      email: new FormControl('', {
        nonNullable: true,
        validators: [Validators.required],
      }),
    });
  }
}
<section class="container-fluid row gap-1">
  <div
    class="container col-5 border shadow rounded-2 p-3"
  >
    <form [formGroup]="userForm"></form>
    {{ userData | async | json }}
  </div>
  <div
    class="container col-5 border shadow rounded-2 p-3"
  ></div>
</section>
